mapper.update:
update entity
set status = -1
, reject_reason = #{rejectReason}
where id = #{id}

mapper.select:
SELECT COUNT(*) FROM entity WHERE 1=1
AND name LIKE CONCAT('%', #{name}, '%')
AND status = #{status}

SELECT *
FROM entity
WHERE 1=1

    AND name LIKE CONCAT('%', #{name}, '%')


    AND status = #{status}

LIMIT #{offset}, #{limit}
